import { SystemProgram } from "@solana/web3.js";
import { createMultisigAddMemberInstruction } from "../generated";
export function multisigAddMember({ multisigPda, configAuthority, rentPayer, newMember, memo, }) {
    return createMultisigAddMemberInstruction({
        multisig: multisigPda,
        configAuthority,
        rentPayer,
        systemProgram: SystemProgram.programId,
    }, { args: { newMember, memo: memo ?? null } });
}
