import BN from "bn.js";
import { createMultisigAddSpendingLimitInstruction, } from "../generated";
export function multisigAddSpendingLimit({ multisigPda, configAuthority, spendingLimit, rentPayer, createKey, vaultIndex, mint, amount, period, members, destinations, memo, }) {
    return createMultisigAddSpendingLimitInstruction({
        multisig: multisigPda,
        spendingLimit,
        configAuthority,
        rentPayer,
    }, {
        args: {
            createKey,
            vaultIndex,
            mint,
            amount: new BN(amount.toString()),
            period,
            members,
            destinations,
            memo: memo ?? null,
        },
    });
}
