import * as transactions from "../transactions";
import { translateAndThrowAnchorError } from "../errors";
/** Create a new vault transactions batch. */
export async function batchCreate({ connection, feePayer, multisigPda, batchIndex, creator, rentPayer, vaultIndex, memo, signers, sendOptions, }) {
    const blockhash = (await connection.getLatestBlockhash()).blockhash;
    const tx = transactions.batchCreate({
        blockhash,
        feePayer: feePayer.publicKey,
        multisigPda,
        batchIndex,
        creator: creator.publicKey,
        rentPayer: rentPayer?.publicKey ?? creator.publicKey,
        vaultIndex,
        memo,
    });
    const allSigners = [feePayer, creator];
    if (signers) {
        allSigners.push(...signers);
    }
    if (rentPayer) {
        allSigners.push(rentPayer);
    }
    tx.sign(allSigners);
    try {
        return await connection.sendTransaction(tx, sendOptions);
    }
    catch (err) {
        translateAndThrowAnchorError(err);
    }
}
