import { TransactionMessage, VersionedTransaction, } from "@solana/web3.js";
import * as instructions from "../instructions";
/**
 * Returns unsigned `VersionedTransaction` that needs to be
 * signed by `creator` and `feePayer` before sending it.
 */
export function configTransactionCreate({ blockhash, feePayer, creator, rentPayer, multisigPda, transactionIndex, actions, memo, }) {
    const message = new TransactionMessage({
        payerKey: feePayer,
        recentBlockhash: blockhash,
        instructions: [
            instructions.configTransactionCreate({
                creator,
                rentPayer,
                multisigPda,
                transactionIndex,
                actions,
                memo,
            }),
        ],
    }).compileToV0Message();
    return new VersionedTransaction(message);
}
